mapper.select:
select providers_id as providersId, provider_name as providerName, profile as profile, phone_no as phoneNo, address as address, logo as logo, type as type, head as head, city as city from tbl_providers_info where serviceType=0 and type in
#{item}
order by order_no LIMIT #{startRow}, #{pageSize}

mapper.update:
update tbl_providers_info 
set
update_time = now()

	,provider_name = #{providerName}


	,profile = #{profile}


	,phone_no = #{phoneNo}


	,address = #{address}


	,logo =#{logo}

where providers_id =#{providersId}
